package hackerrank.palindrome;

import java.io.IOException;


class Result {

    /*
     * Complete the 'palindromeIndex' function below.
     *
     * The function is expected to return an INTEGER.
     * The function accepts STRING s as parameter.
     *
     * 문자열 하나를 빼서 palindrome 이 되는 index 를 찾아라
     * 타임아웃을 피하기 위해 앞/뒤 동시 확인
     *
     *
     */

    public static int palindromeIndex(String s) {
        // Write your code here
        if (isPalindrome(s)) {
            return -1;
        }

        int index = -1;
        for (int i = 0; i < s.length() / 2; i++) {
            if (s.charAt(i) != s.charAt(s.length() - i - 1)) {
                index = i;
                break;
            }
        }

        String removedString = s.substring(0, index) + s.substring(index + 1);
        String removedString2 = s.substring(0, s.length() - index - 1) + s.substring(s.length() - index - 1 + 1);

        if (isPalindrome(removedString)) {
            return index;
        }
        if (isPalindrome(removedString2)) {
            return s.length() - index - 1;
        }

        return -1;
    }

    private static boolean isPalindrome(String s) {
        System.out.println("s: " + s + ", length: " + s.length());
        for (int i = 0; i < s.length() / 2; i++) {
            System.out.println("index: " + i + " re: " + (s.length() - i - 1));
            if (s.charAt(i) != s.charAt(s.length() - i - 1)) {
                return false;
            }
        }
        return true;
    }
}

